class class1:
    def __init__(self):
        self.a=10
        self.b=20
        print("a:", self.a, "b:", self.b)

class class2(class1):
    def __init__(self):
        self.a=10
        self.b=20
        print("c:", self.a, "d:", self.b)
        super(class2, self).__init__()

class class3(class1):
    def __init__(self):
        self.a=10
        self.b=20
        print("x:", self.a, "y:", self.b)
        super().__init__()



class class4(class3,class2):
    def __init__(self):
        self.a=10
        self.b=20
        print("e:", self.a, "f:", self.b)
        super().__init__()




ob1=class4()
print(class4.__mro__)




