n=int(input())
li=[]
if n>1 and n<100:
    while(n>0):

        ele=int(input())
        if ele>-100 and ele<100:
            li.append(ele)
        n=n-1

size=len(li)
result=1
sum=0
i=0
while i<size:
    sum=li[i]
    for j in range(i+1,size):
        if result==0:
            break
        else:
            sum=sum+li[j]
            result=sum

    i+=1
if result==0:
    print("1")
else:
    print("0")

